/* Scanner tables */
 
#include "stables.h"
 
/* Local static variables */
 
static selement_t *stack;
static selement_t *frees = NULL;
static int err_flag;
static int relocatable;
static int defer;
static int pcinc;
static int entsize;
 
/* External variables */
 
extern int pc;
